import React, { useState, useEffect } from 'react';

import axios from 'axios';

const client = axios.create({
	baseURL: 'https://jsonplaceholder.typicode.com/todos',
});

const App = () => {
	const [title, setTitle] = useState('');
	const [completed, setCom] = useState(false);
	const [todos, setTodos] = useState([]);

	// GET with Axios
	useEffect(() => {
    client.get().then((response) => {setTodos(response.data);});
     }, []);


	// DELETE with Axios
	const deleteTodo = (id) => {
    client.delete(`${id}`);
    setTodos(
       todos.filter((post) => {
          return post.id !== id;
       })
    );
 };

	// handle form submission
	const handleSubmit = (e) => {
		e.preventDefault();
		addTodos(title, completed);
	};

	// POST with Axios
	const addTodos = (title, completed) => {
    client
       .post('', {
          title: title,
          completed: completed,
       })
       .then((response) => {
          setTodos([response.data, ...todos]);
       });
    setTitle('');
    setCom(false);
 };

	return (
		<div className="app">
			<nav>
				<h1>Todos APP</h1>
			</nav>
			<div className="add-todo-container">
				<form onSubmit={handleSubmit}>
					<input
						type="text"
						className="form-control"
						value={title}
						onChange={(e) => setTitle(e.target.value)}
					/>
					<textarea
						name=""
						className="form-control"
						id=""
						cols="10"
						rows="8"
						value={completed}
						onChange={(e) => setCom(e.target.value)}
					></textarea>
					<button type="submit">Add Task</button>
				</form>
			</div>
			<div className="todos-container">
				<h2>All Todos ðŸ“«</h2>
				{todos.map((todo) => {
					return (
						<div className="todo-card" key={todo.id}>
							<h2 className="todo-title">{todo.title}</h2>
							{/* <p className="todo-com">{todo.completed}</p> */}
							<div className="button">
								<div className="delete-btn" onClick={() => deleteTodo(todo.id)}>
									Delete
								</div>
							</div>
						</div>
					);
				})}
			</div>
		</div>
	);
};

export default App;
