import React, { useState, useEffect } from 'react';

import axios from 'axios';

const client = axios.create({
	baseURL: 'http://localhost:3006/todos',
});

const App = () => {
	const [title, setTitle] = useState('');
	const [completed, setCom] = useState(Boolean(false));
	const [todos, setTodos] = useState([]);

	// GET with Axios
	useEffect(() => {
    client.get().then((response) => {setTodos(response.data);});
     }, []);


	// DELETE with Axios
	const deleteTodo = (id) => {
    client.delete(`${id}`);
    setTodos(
       todos.filter((post) => {
          return post.id !== id;
       })
    );
 };

 const UpdateTodo = (id) =>{
  e.preventDefault()

 }

	// handle form submission
	const handleSubmit = (e) => {
		e.preventDefault();
		addTodos(title, completed);
	};

	// POST with Axios
	const addTodos = (title, completed) => {
    client
       .post('', {
          title: title,
          completed: completed,
       })
       .then((response) => {
          setTodos([response.data, ...todos]);
       });
    setTitle('');
    setCom(false);
 };

 const [isShown, setIsShown] = useState(false);

const handleClick = event => {
  // üëáÔ∏è toggle shown state
  setIsShown(current => !current);

  // üëáÔ∏è or simply set it to true
  // setIsShown(true);
};

	return (
		<div className="app">
			<nav>
				<h1>Todos APP</h1>
        <button onClick={handleClick} className="button-6" style={{textAlign:'center',fontSize:'15px'}}>ADD</button>
			</nav>
			{ isShown &&(
        <div className="add-todo-container">
				<form onSubmit={handleSubmit}>
					<input
						type="text"
						className="form-control"
						value={title}
            placeholder="Task"
						onChange={(e) => setTitle(e.target.value)}
					/>
					Completed: <input type="checkbox"
						name=""
						className="form-control"
						id=""
						cols="10"
						rows="8"
						value={completed}
						onChange={(e) => setCom(e.target.value)}
					></input>
					<button type="submit">Add Task</button>
				</form>
        <hr/>
			</div>
      )}
			<div className="todos-container">
				<h2 style={{textAlign:"center"}}>ALL TODOS</h2>
				{todos.map((todo) => {
					return (
						<div className="todo-card" key={todo.id}>
              <h2>{todo.id}</h2>
							<h2 className="todo-title">{todo.title}</h2>
							{/* <p className="todo-com">{todo.completed}</p> */}
							<div className="button">
								<div className="delete-btn" style={{display:"inline-block"}} onClick={() => deleteTodo(todo.id)}>
									Delete
								</div>
                <div className="delete-btn" style={{display:"inline-block"}} onClick={() => {
                  <form>
                    <input type="text" className="form-control"
						          value={title}
                      placeholder="Task"
                      onChange={(e) => setTitle(e.target.value)}/>
                    Completed: <input type="checkbox"
						name=""
						className="form-control"
						id=""
						cols="10"
						rows="8"
						value={completed}
						onChange={(e) => setCom(e.target.value)}
					></input>
                  </form>
                }}>
									Update
								</div>
							</div>
						</div>
					);
				})}
			</div>
		</div>
	);
};

export default App;
