import React, { useState, useEffect } from 'react';

import axios from 'axios';

import UpdateTdo from './UpdateTodo';

const client = axios.create({
	baseURL: 'http://localhost:3006/todos',
});

const App = () => {
	const [title, setTitle] = useState('');
	const [completed, setCom] = useState(Boolean(false));
	const [todos, setTodos] = useState([]);

	// GET with Axios
	useEffect(() => {
    client.get().then((response) => {setTodos(response.data);});
     }, []);


	// DELETE with Axios
	const deleteTodo = (id) => {
    client.delete(`${id}`);
    setTodos(
       todos.filter((todo) => {
          return todo.id !== id;
       })
    );
 };



	// handle form submission
	const handleSubmit = (e) => {
		e.preventDefault();
		addTodos(title, completed);
	};

//   const handleSubmit2 = (e) => {
// 		e.preventDefault();
// 		UpdateTodo(title, completed);
// 	};

	// const UpdateTodo = () =>{
	// 	client.put()

	//    }

	// POST with Axios
	const addTodos = (title, completed) => {
    client
       .post('', {
          title: title,
          completed: completed,
       })
       .then((response) => {
          setTodos([response.data, ...todos]);
       });
    setTitle('');
    setCom(false);
 };

